const { useState, useEffect } = React;
const { createRoot } = ReactDOM;

function ServiceForm({ addService, currentService, updateService }) {
  const [name, setName] = useState(currentService ? currentService.name : '');
  const [description, setDescription] = useState(currentService ? currentService.description : '');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (currentService) {
      updateService({ name, description });
    } else {
      addService({ name, description });
    }
    setName('');
    setDescription('');
  };

  useEffect(() => {
    if (currentService) {
      setName(currentService.name);
      setDescription(currentService.description);
    }
  }, [currentService]);

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Nome do Serviço"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <input
        type="text"
        placeholder="Descrição do Serviço"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <button type="submit">{currentService ? 'Atualizar Serviço' : 'Adicionar Serviço'}</button>
    </form>
  );
}

function ServiceList({ services, deleteService, editService }) {
  return (
    <div>
      <h2>Lista de Serviços</h2>
      <ul>
        {services.map((service, index) => (
          <li key={index}>
            <div>
              <strong>{service.name}</strong>
              <p>{service.description}</p>
            </div>
            <div>
              <button onClick={() => editService(index)}>Editar</button>
              <button onClick={() => deleteService(index)}>Excluir</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

function App() {
  const [services, setServices] = useState([]);
  const [currentServiceIndex, setCurrentServiceIndex] = useState(null);

  const addService = (service) => {
    setServices([...services, service]);
  };

  const deleteService = (index) => {
    setServices(services.filter((_, i) => i !== index));
  };

  const editService = (index) => {
    setCurrentServiceIndex(index);
  };

  const updateService = (updatedService) => {
    const updatedServices = services.map((service, index) =>
      index === currentServiceIndex ? updatedService : service
    );
    setServices(updatedServices);
    setCurrentServiceIndex(null);
  };

  return (
    <div className="App">
      <h1>Gerenciador de Serviços</h1>
      <ServiceForm
        addService={addService}
        currentService={currentServiceIndex !== null ? services[currentServiceIndex] : null}
        updateService={updateService}
      />
      <ServiceList services={services} deleteService={deleteService} editService={editService} />
    </div>
  );
}

const rootElement = document.getElementById('root');
const root = createRoot(rootElement);
root.render(<App />);
